class Persona{final int? codPersona;final String nombre;final String apellidos;final String? telefono;final String? email;Persona({this.codPersona,required this.nombre,required this.apellidos,this.telefono,this.email});factory Persona.fromMap(Map<String,dynamic> m)=>Persona(codPersona:m['cod_persona']as int?,nombre:m['nombre'],apellidos:m['apellidos'],telefono:m['telefono'],email:m['email']);Map<String,dynamic> toMap()=>{'cod_persona':codPersona,'nombre':nombre,'apellidos':apellidos,'telefono':telefono,'email':email};}